mapper.select:
SELECT

FROM express a

WHERE 1 = 1

    AND a.id = #{id}


    AND a.code = #{code}

SELECT

FROM express a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}


        ORDER BY ${orderBy}


        ORDER BY a.update_date DESC

mapper.delete:
UPDATE express SET
    del_flag = #{DEL_FLAG_DELETE},
    update_date = #{updateDate}
WHERE id = #{id}
