mapper.select:
SELECT

FROM franchise a

WHERE a.id = #{id}

SELECT

FROM franchise a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}


        ORDER BY ${orderBy}


        ORDER BY a.update_date DESC

mapper.delete:
UPDATE franchise SET
    del_flag = #{DEL_FLAG_DELETE},
    update_date = #{updateDate}
WHERE id = #{id}
