mapper.select:
SELECT

FROM franchise a

WHERE a.id = #{id}

SELECT

FROM franchise a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

    AND a.user_id = #{userId}



        ORDER BY ${orderBy}


        ORDER BY INSTR('驳回、通过', a.status), a.create_date DESC

mapper.delete:
UPDATE franchise SET
    del_flag = #{DEL_FLAG_DELETE},
    update_date = #{updateDate}
WHERE id = #{id}
